mapper.select:
SELECT
i.`item_id`,i.`item_image_backup`,i.`item_no`,i.`item_url`,i.`item_name`,i.`item_md`,i.`item_title`,i.`item_type`,i.`item_hdlx`,i.`item_yjbl`,s.`username`,
i.`createtime`,i.`updatetime`,i.`item_qhjg`,i.`state`,i.`item_fwdj`,c.`coupon_url`,c.`coupon_start_time`,c.`coupon_end_time`,c.`coupon_get_num`,
c.`coupon_rest_num`,c.`coupon_use_num`,i.`item_image`,i.`qq`,i.`remark`,i.`item_image_backup`,i.`item_website_type`,i.`item_jhlb`,i.`item_jhlj`
FROM
(SELECT * FROM t_item WHERE 1=1
and state = #{state} 
and item_type = #{vo.item_type} 
and item_no = #{vo.item_no} 
and qq LIKE CONCAT('%', #{vo.qq},'%')
 and item_name like CONCAT('%',#{vo.item_name},'%') 

	and createtime>=#{vo.query_start_time}


	and createtime<= DATE_ADD(#{vo.query_end_time},INTERVAL 1 DAY)


AND
user_id IN(SELECT user_id FROM
t_sys_user
WHERE 1=1
and user_id = #{sa.user_id} 
and team_id = #{sa.team_id} 
and team_id = #{vo.team_id} 
and user_id = #{vo.user_id} 
))i
INNER JOIN t_coupon c ON i.`coupon_id`=c.`coupon_id`
JOIN
t_sys_user s ON
i.`user_id`=s.`user_id`

1=1

	and c.coupon_get_num>=#{vo.coupon_get_num_min}


	and c.coupon_get_num<=#{vo.coupon_get_num_max}


	and c.activity_start_time >= #{vo.activity_begin_time}


	and c.activity_start_time <= #{vo.activity_end_time}


ORDER BY i.`updatetime` DESC
LIMIT
#{pagestart},#{rows}

SELECT
count(*)
FROM
(SELECT * FROM t_item WHERE 1=1
and state = #{state} 
and item_type = #{vo.item_type} 
and item_no = #{vo.item_no} 
and qq LIKE CONCAT('%', #{vo.qq},'%')
 and item_name like CONCAT('%',#{vo.item_name},'%') 

	and createtime>=#{vo.query_start_time}


	and createtime<= DATE_ADD(#{vo.query_end_time},INTERVAL 1 DAY)


AND
user_id IN(SELECT user_id FROM
t_sys_user
WHERE 1=1
and user_id = #{sa.user_id} 
and team_id = #{sa.team_id} 
and team_id = #{vo.team_id} 
and user_id = #{vo.user_id} 
))i
INNER JOIN t_coupon c ON i.`coupon_id`=c.`coupon_id`
JOIN
t_sys_user s ON
i.`user_id`=s.`user_id`

	1=1

		and c.coupon_get_num>=#{vo.coupon_get_num_min}


		and c.coupon_get_num<=#{vo.coupon_get_num_max}


		and c.activity_start_time >= #{vo.activity_begin_time}


		and c.activity_start_time <= #{vo.activity_end_time}

mapper.update:
update t_item
updatetime=now(),
remark=#{audit_remarks},
item_zfje_state='0',
state=#{after_audit_status}
where item_id=#{item_id}

update t_item
updatetime=now(),
remark=#{audit_remarks},
state=#{after_audit_status}, item_ssje=#{item_ssje}
where item_id=#{item_id}

mapper.select:
SELECT '000' state,COUNT(*) num
FROM
(SELECT * FROM t_item WHERE 1=1 AND
user_id IN(SELECT user_id FROM t_sys_user
WHERE 1=1
and user_id = #{user_id} 
and team_id = #{team_id} 
))i
UNION ALL
SELECT d.state,COUNT(i.`state`) num
FROM t_item_state_dic d
LEFT JOIN (SELECT * FROM t_item WHERE 1=1 AND user_id IN(SELECT
user_id
FROM t_sys_user WHERE 1=1
and user_id = #{user_id} 
and team_id = #{team_id} 
))i ON d.`state`=i.`state`
GROUP BY
d.state

SELECT u.`username`,COUNT(*) totle,COUNT(CASE WHEN i.state='001' THEN i.state END) dscnum,COUNT(CASE WHEN i.state='002' THEN i.state END)desnum, COUNT(CASE WHEN i.state='003' THEN i.state END) tgznum, COUNT(CASE WHEN i.state='009' THEN i.state END)jjjsnum, COUNT(CASE WHEN i.state='004' THEN i.state END)yjsnum, COUNT(CASE WHEN i.state='005' THEN i.state END) dfknum,COUNT(CASE WHEN i.state='006' THEN i.state END)fkznum, COUNT(CASE WHEN i.state='007' THEN i.state END) yfknum,COUNT(CASE WHEN i.state='112' THEN i.state END)bhnum, COUNT(CASE WHEN i.state='667' THEN i.state END) jjfknum,COUNT(CASE WHEN i.state='999' THEN i.state END)jjnum, FORMAT(COUNT(*)/(DATEDIFF(MAX(i.`createtime`),MIN(i.`createtime`))+1),2) daynum,FORMAT(COUNT(CASE WHEN i.state='999' THEN i.state END)/COUNT(*)*100,2) jjl, SUM(CASE WHEN i.state='007' THEN item_ssje END) ssje,SUM(item_zfje/item_qhjg*item_fwdj) ysje,FORMAT(SUM(CASE WHEN i.state='007' THEN item_ssje END)/COUNT(CASE WHEN i.state='007' THEN i.state END),2) kdj FROM (SELECT * FROM t_item WHERE 1=1
and createtime>=#{vo.create_start_time}
and createtime<=#{vo.create_end_time}
and updatetime>=#{vo.update_start_time}
and updatetime<=#{vo.update_end_time}
AND user_id IN(SELECT user_id FROM t_sys_user WHERE state!=0 and user_id = #{sa.user_id} and team_id = #{sa.team_id} and team_id = #{vo.team_id} and user_id = #{vo.user_id} ))i LEFT JOIN t_sys_user u ON i.`user_id`=u.`user_id` GROUP BY u.`username`
ORDER BY ssje desc
ORDER BY ssje asc
ORDER BY totle desc
ORDER BY totle asc
LIMIT #{pagestart},#{rows}

SELECT
COUNT(*)
FROM(
SELECT i.user_id
FROM
(SELECT * FROM
t_item WHERE 1=1

	and createtime>=#{vo.create_start_time}


	and createtime<=#{vo.create_end_time}


	and updatetime>=#{vo.update_start_time}


	and updatetime<=#{vo.update_end_time}

AND
user_id IN(SELECT user_id FROM
t_sys_user
WHERE state!=0
and user_id = #{sa.user_id} 
and team_id = #{sa.team_id} 
and team_id = #{vo.team_id} 
and user_id = #{vo.user_id} 
))i
GROUP BY
i.`user_id`)t
